com.mirth.connect.plugins.javascriptstep.script:
// checks all bundle entries for the patient
// checks all the identifiers of the patient to get the XLR id
// adds the patient_id variable to the channel map for metadata mapping
msg['entry'].forEach((entry) => {

  if (entry['resource']['resourceType'] == 'Patient')
  {
    entry['resource']['identifier'].forEach((identifier) => {

      if (identifier['system'] == 'https://careflow.poqi.ca/patient-identifier')
      {
        // mapp for channel metadata
        channelMap.put('patient_id', identifier['value']);

        tmp['userIdentifiers'][0]['type']  = identifier['system'];
        tmp['userIdentifiers'][0]['value'] = identifier['value'];
      }
    });

    tmp['firstName']   = entry['resource']['name'][0]['given'][0];
    tmp['lastName']    = entry['resource']['name'][0]['family'];
    tmp['dateOfBirth'] = entry['resource']['birthDate'];
    tmp['gender']      = entry['resource']['gender'];

    // map phone and email if present
    // TODO: phone should be mandatory
    entry['resource']['telecom'].forEach((telecom) => {

      if (telecom['system'] == 'phone')
      {
        tmp['phoneNumber'] = telecom['value'];
      }
      else if (telecom['system'] == 'email')
      {
        tmp['email'] = telecom['value'];
      }
    });
  }
  else if (entry['resource']['resourceType'] == 'SupplyDelivery')
  {
    entry['resource']['identifier'].forEach((identifier) => {

      if (identifier['system'] == 'http://clouddx.com/kit-identifier')
      {
        tmp['serialNumber'] = identifier['value'];
      }
    });

    tmp['onboardingDate'] = entry['resource']['occurrenceDateTime'];
  }
});

batchProperties.batchScript:
var queue = $gc('queue');
if (!queue)
{
  queue = new java.util.LinkedList();
  var obj = JSON.parse(org.apache.commons.io.IOUtils.toString(reader));
  for each (record in obj.records)
  {
    queue.add(JSON.stringify(record.message)); // record.message is the bundle
  }
  $gc('queue', queue);
}

var message = queue.poll();
if (!message)
{
  globalChannelMap.remove('queue');
}
return message;

com.mirth.connect.plugins.javascriptstep.script:
// NOTE: the message template data type is "raw" so 4xx and 5xx response codes still execute this code
//       (those could come without a payload, making the response transformer to not be executed).

//logger.info($('responseStatusLine'));
//logger.info(httpResponseStatusCode);// not defined
//logger.info(msg);
//logger.info(typeof msg);

var statusLine = $('responseStatusLine'); // HTTP/1.1 200 OK

if (statusLine)
{
   var parts = statusLine.split(' ');
   if (parts.length >= 2)
   {
      var responseCode = parseInt(parts[1], 10);

      // Force 4xx responses to error
      if (responseCode >= 400 && responseCode < 500)
      {
         responseStatus = ERROR;
         responseStatusMessage = statusLine;
         responseErrorMessage = msg;
         //tmp = msg; // this doesn't work // the channel message is now the error message
      }
      // Force 5xx responses to error
      else if (responseCode > 500)
      {
         responseStatus = ERROR;
         responseStatusMessage = statusLine;
         responseErrorMessage = msg;
      }
   }
}

