properties.code:
/**
 * Obtiene un objeto con los datos del paciente extraidos de un mensaje HL7 v2.x
 */
function get_patient_data(msg)
{
    var data = {};

    data.nacimiento = msg["PID"]["PID.7"]["PID.7.1"].toString();
    data.nombre = msg["PID"]["PID.5"]["PID.5.2"].toString();
    data.apellido = msg["PID"]["PID.5"]["PID.5.1"].toString();
    data.sexo = msg["PID"]["PID.8"]["PID.8.1"].toString();

    return data;
}

/**
 * Devuelve true si el mensaje es ADT, false si no. Se utilizar en filtros.
 */
function es_adt(msg)
{
   return msg["MSH"]["MSH.9"]["MSH.9.1"].toString() == "ADT";
}

